import React from 'react';
import '../post/Post-style.css';


const Post = (props) => {

    return(
        <div className="post">
            <div className="anakyn-Post">
                <div className="author-Info">
                    <img className="pic" src={props.author.photo}></img>
                    <div className="nickname">
                        <p className="data">{props.author.name}</p>
                        <p className="nick">{props.author.nickname}</p>
                        <p className="data">{props.dates}</p>
                    </div>
                </div>
                <div className="post-Info">
                    <p className="content">{props.content}</p>
                    <img className="post-Image" src={props.image}></img>
                </div>
            </div>
        </div>
    )
}

export default Post;
